import Form from './components/form'
import Header from './components/header'
import List from './components/list';
import React, {useState} from 'react';
function App() {
  //Now let's start by creating the list of the todos
  //we will use reacts hooks for that
  // let's import the useState hook
  const [todos, setTodos] = useState([])
  const changeNameWork = (event) => {
    //lay gia tri nguoi dung nhap vao input
    let val = event.target.value;
    // console.log(val);
    //cap nhat state
    this.setState({ nameWork: val });
  };
  addToDoWork = () => {
    // add state nameWork vaof state datawork
    let nameToDo = this.state.nameWork.trim();
    if (nameToDo.length > 0) {
      //xu ly state
      // xu li id
      this.setState((state) => ({
        idToDo: state.idToDo + 1,
      }));
      // tao danh sach cong viec
      this.setState((state) => ({
        dataToDo: [
          ...state.dataToDo,
          {
            idToDo: state.idToDo,
            name: state.nameWork,
            done: false,
          },
        ],
      }));
      //xoa bo ten cong viec
      this.setState({ nameWork: "" });
    }
  };

  return (
    <div className="App">
      <Header/>
      <Form setTodo={setTodos} len={todos.length}/>
      <List todoList={todos} setTodo={setTodos}/>
    </div>
  );
}

export default App;
